import React, { useState } from 'react';
import styled, { keyframes } from 'styled-components';
import SmallBubble from './SmallBubble';

const pulse = keyframes`
  0% {
    transform: scale(1);
    box-shadow: 0 0 5px rgba(0, 123, 255, 0.5);
  }
  50% {
    transform: scale(1.05);
    box-shadow: 0 0 20px rgba(0, 123, 255, 1);
  }
  100% {
    transform: scale(1);
    box-shadow: 0 0 5px rgba(0, 123, 255, 0.5);
  }
`;

const Bubble = styled.div`
  width: ${props => props.size}px;
  height: ${props => props.size}px;
  background-image: url(${props => props.image});
  background-size: cover;
  background-position: center;
  border-radius: 50%;
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  position: absolute;
  top: ${props => props.position.y}px;
  left: ${props => props.position.x}px;
  transition: transform 0.3s, opacity 0.3s;
  animation: ${pulse} 2s infinite;

  &:hover {
    transform: scale(1.1);
  }
`;

const SmallBubblesContainer = styled.div`
  position: absolute;
  top: ${props => props.position.y}px;
  left: ${props => props.position.x}px;
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  width: 400px;
  transform: translate(-50%, -50%);
  z-index: 1;
`;

const LargeBubble = ({ skill, subSkills, image, size, position }) => {
  const [expanded, setExpanded] = useState(false);

  const handleClick = () => {
    setExpanded(true);
  };

  return (
    <>
      {!expanded && (
        <Bubble onClick={handleClick} image={image} size={size} position={position}>
          {skill}
        </Bubble>
      )}
      {expanded && (
        <SmallBubblesContainer position={{ x: position.x + size / 2, y: position.y + size / 2 }}>
          {subSkills.map((subSkill, index) => (
            <SmallBubble key={index} skill={subSkill} image={image} />
          ))}
        </SmallBubblesContainer>
      )}
    </>
  );
};

export default LargeBubble;
