import React, { useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';
import SmallBubble from './SmallBubble';
import { getRandomInt, generateRandomPositions } from './utils';

const pulse = keyframes`
  0% {
    transform: scale(1);
    box-shadow: 0 0 5px rgba(0, 123, 255, 0.5);
  }
  50% {
    transform: scale(1.05);
    box-shadow: 0 0 20px rgba(0, 123, 255, 1);
  }
  100% {
    transform: scale(1);
    box-shadow: 0 0 5px rgba(0, 123, 255, 0.5);
  }
`;

const Bubble = styled.div.attrs(props => ({
  style: {
    width: `${props.size}px`,
    height: `${props.size}px`,
    top: `${props.position.y}px`,
    left: `${props.position.x}px`,
    backgroundImage: `url(${props.image})`,
  }
}))`
  background-size: cover;
  background-position: center;
  border-radius: 50%;
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  position: absolute;
  transition: transform 0.3s, opacity 0.3s;
  animation: ${pulse} 2s infinite;

  &:hover {
    transform: scale(1.1);
  }
`;

const SmallBubblesContainer = styled.div`
  position: absolute;
  width: ${props => props.size * 2}px;
  height: ${props => props.size * 2}px;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
`;

// HighlightArea component to visually indicate the available area
const HighlightArea = styled.div`
  position: absolute;
  width: 100%;
  height: 100%;
  border: 2px dashed rgba(255, 255, 255, 0.5);
  border-radius: 50%;
  top: 0;
  left: 0;
  pointer-events: none;
`;

const LargeBubble = ({ skill, subSkills, image, size, position, existingPositions }) => {
  const [expanded, setExpanded] = useState(false);
  const [smallBubblePositions, setSmallBubblePositions] = useState([]);

  useEffect(() => {
    if (expanded) {
      console.log(`Generating positions for ${subSkills.length} small bubbles around the large bubble of size ${size}px`);

      const newPositions = generateRandomPositions(size, size, size * 2, size * 2, subSkills.length);
      const newSizes = subSkills.map(() => getRandomInt(30, 50));

      setSmallBubblePositions(newPositions.map((pos, index) => ({
        ...pos,
        x: pos.x - size / 2,
        y: pos.y - size / 2,
        size: newSizes[index]
      })));
    }
  }, [expanded, subSkills, size, existingPositions]);

  const handleClick = () => {
    setExpanded(true);
  };

  return (
    <>
      {!expanded && (
        <Bubble onClick={handleClick} size={size} position={position} image={image}>
          {skill}
        </Bubble>
      )}
      {expanded && (
        <SmallBubblesContainer size={size}>
          <HighlightArea />
          {smallBubblePositions.length > 0 && subSkills.map((subSkill, index) => (
            <SmallBubble key={index} skill={subSkill} position={smallBubblePositions[index]} />
          ))}
        </SmallBubblesContainer>
      )}
    </>
  );
};

export default LargeBubble;
