import React from 'react';
import styled from 'styled-components';

const Bubble = styled.div`
  width: 70px;
  height: 70px;
  background-color: rgba(0, 200, 100, 0.7);
  border-radius: 50%;
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 10px;
  cursor: pointer;
  transition: transform 0.3s;
  &:hover {
    transform: scale(1.1);
  }
`;

const SmallBubble = ({ skill }) => {
  return (
    <Bubble>
      {skill}
    </Bubble>
  );
};

export default SmallBubble;
