import React, { useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';
import SmallBubble from './SmallBubble';
import { getRandomInt, generateNonOverlappingPosition } from './utils';  // Make sure to create and import these utility functions

// Define the animation for the large bubble
const pulse = keyframes`
  0% {
    transform: scale(1);
    box-shadow: 0 0 5px rgba(0, 123, 255, 0.5);
  }
  50% {
    transform: scale(1.05);
    box-shadow: 0 0 20px rgba(0, 123, 255, 1);
  }
  100% {
    transform: scale(1);
    box-shadow: 0 0 5px rgba(0, 123, 255, 0.5);
  }
`;

// Define the style for the large bubble
const Bubble = styled.div`
  width: ${props => props.size}px;
  height: ${props => props.size}px;
  background-image: url(${props => props.image});
  background-size: cover;
  background-position: center;
  border-radius: 50%;
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  position: absolute;
  top: ${props => props.position.y}px;
  left: ${props => props.position.x}px;
  transition: transform 0.3s, opacity 0.3s;
  animation: ${pulse} 2s infinite;

  &:hover {
    transform: scale(1.1);
  }
`;

// Define the container for small bubbles
const SmallBubblesContainer = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  width: ${props => props.size}px;
  height: ${props => props.size}px;
  transform: translate(-50%, -50%);
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
`;

// Define the LargeBubble component
const LargeBubble = ({ skill, subSkills, image, size, position }) => {
  const [expanded, setExpanded] = useState(false);
  const [smallBubblePositions, setSmallBubblePositions] = useState([]);

  useEffect(() => {
    if (expanded) {
      const containerSize = { width: size, height: size };
      const newPositions = [];
      const newSizes = [];

      // Generate random positions and sizes for small bubbles
      subSkills.forEach(() => {
        const newSize = getRandomInt(30, 50);  // Random size for small bubbles
        const newPosition = generateNonOverlappingPosition(newSize, newPositions, containerSize);
        newPositions.push(newPosition);
        newSizes.push(newSize);
      });

      // Ensure positions include size
      setSmallBubblePositions(newPositions.map((pos, index) => ({ ...pos, size: newSizes[index] })));
    }
  }, [expanded, subSkills, size]);

  const handleClick = () => {
    setExpanded(true);
  };

  return (
    <>
      {!expanded && (
        <Bubble onClick={handleClick} image={image} size={size} position={position}>
          {skill}
        </Bubble>
      )}
      {expanded && (
        <SmallBubblesContainer size={size}>
          {smallBubblePositions.length > 0 && subSkills.map((subSkill, index) => (
            <SmallBubble key={index} skill={subSkill} position={smallBubblePositions[index]}  image={image}/>
          ))}
        </SmallBubblesContainer>
      )}
    </>
  );
};

export default LargeBubble;
