import React from 'react';
import { Link } from 'react-router-dom';
import '../css/ProjectCard.css';

function ProjectCard({ media, link, title }) {
  // Determine if the media is a video based on the file extension
  const isVideo = media.endsWith('.mp4') || media.endsWith('.webm') || media.endsWith('.ogg');

  return (
    <div className="project-card">
      {isVideo ? (
        <video className="project-media" autoPlay loop muted>
          <source src={media} type="video/mp4" />
          <source src={media} type="video/webm" />
          <source src={media} type="video/ogg" />
          Your browser does not support the video tag.
        </video>
      ) : (
        <div className="project-media" style={{ backgroundImage: `url(${media})` }}></div>
      )}
      <Link to={link} className="project-link">{title}</Link>
    </div>
  );
}

export default ProjectCard;
