import React, { useState } from 'react';
import styled from 'styled-components';
import SmallBubble from './SmallBubble';

const Bubble = styled.div`
  width: 150px;
  height: 150px;
  background-color: rgba(0, 123, 255, 0.7);
  border-radius: 50%;
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  position: relative;
  transition: transform 0.3s;
  &:hover {
    transform: scale(1.1);
  }
`;

const SmallBubblesContainer = styled.div`
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  width: 400px;
`;

const LargeBubble = ({ skill, subSkills }) => {
  const [expanded, setExpanded] = useState(false);

  return (
    <Bubble onClick={() => setExpanded(!expanded)}>
      {skill}
      {expanded && (
        <SmallBubblesContainer>
          {subSkills.map((subSkill, index) => (
            <SmallBubble key={index} skill={subSkill} />
          ))}
        </SmallBubblesContainer>
      )}
    </Bubble>
  );
};

export default LargeBubble;
