// utils.js

// Generate a random integer between min and max (inclusive)
export const getRandomInt = (min, max) => Math.floor(Math.random() * (max - min + 1)) + min;

export const generateNonOverlappingPosition = (size, existingPositions, containerSize, originalPosition) => {
  let position;
  let overlap;
  const maxAttempts = 1000;
  let attempts = 0;

  console.log(`Generating non-overlapping position for size ${size} within container ${containerSize.width}x${containerSize.height}`);

  do {
    overlap = false;

    let xmax = containerSize.width/2 +originalPosition.x - size;
    let ymax = containerSize.height/2+originalPosition.y - size;
    
    position = {
      x: getRandomInt(originalPosition.x, xmax),
      y: getRandomInt(originalPosition.y, ymax)
    };

    for (let existing of existingPositions) {
      if (Math.abs(position.x - existing.x) < size && Math.abs(position.y - existing.y) < size) {
        overlap = true;
        break;
      }
    }
    console.log(`Generated position: ${position.x}, ${position.y} after ${attempts} attempts for large bubble at ${originalPosition.x}, ${originalPosition.y}, ymax is ${ymax}, xmax is ${xmax}, ymin is ${ymin}, xmin is ${xmin}`);

    attempts++;
  } while (overlap && attempts < maxAttempts);


  return position;
};
