import React, { useState } from 'react';
import './LargeBubble.css';

const LargeBubble = () => {
  const [bubbles, setBubbles] = useState([
    { id: 1, text: 'Artificial Intelligence', subSkills: ['PyTorch', 'Deep Learning', 'TensorFlow'] },
    { id: 2, text: 'Web Development', subSkills: ['React', 'Vue.js', 'Angular'] },
    // Add more large bubbles as needed
  ]);

  const [selectedBubble, setSelectedBubble] = useState(null);

  const handleBubbleClick = (bubble) => {
    setSelectedBubble(bubble);
  };

  return (
    <div className="bubble-container">
      {bubbles.map((bubble) => (
        <div
          key={bubble.id}
          className={`large-bubble ${selectedBubble === bubble ? 'fade-out' : ''}`}
          onClick={() => handleBubbleClick(bubble)}
        >
          {bubble.text}
        </div>
      ))}
      {selectedBubble && (
        <div className="small-bubble-container">
          {selectedBubble.subSkills.map((skill, index) => (
            <div key={index} className="small-bubble">
              {skill}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default BubbleAnimation;
