export const getRandomInt = (min, max) => Math.floor(Math.random() * (max - min + 1)) + min;

export const generateNonOverlappingPosition = (size, existingPositions, containerSize) => {
  let position;
  let overlap;
  const maxAttempts = 1000;
  let attempts = 0;
  console.log('containerSize', containerSize);
  do {
    overlap = false;
    position = {
      x: getRandomInt(containerSize.width - size, containerSize.width + size),
      y: getRandomInt(containerSize.height - size, containerSize.height + size)
    };
    for (let existing of existingPositions) {
      if (Math.abs(position.x - existing.x) < size && Math.abs(position.y - existing.y) < size) {
        overlap = true;
        break;
      }
    }
    attempts++;
  } while (overlap && attempts < maxAttempts);

  return position;
};
