import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import LargeBubble from './LargeBubble';
import bubble from './bubble.png';
//import { getRandomInt, generateNonOverlappingPosition } from './utils';  // Make sure to create and import these utility functions

const Container = styled.div`
  position: relative;
  width: 100vw;
  height: 100vh;
  background-color: #282c34;
  overflow: hidden;
`;

const skills = [
  {
    skill: 'Artificial Intelligence',
    subSkills: ['Data Analysis', 'Computer Vision', 'PyTorch', 'Deep Learning'],
    image: bubble
  },
  {
    skill: 'Web Development',
    subSkills: ['HTML', 'CSS', 'JavaScript', 'React', 'Node.js'],
    image: bubble
  },
  {
    skill: 'Game Development',
    subSkills: ['Unity', 'Unreal Engine', 'C#', '3D Modeling'],
    image: bubble
  }
];

export function Skill() {
  const [positions, setPositions] = useState([]);

  useEffect(() => {
    const containerSize = { width: window.innerWidth, height: window.innerHeight };
    const newPositions = [];
    const sizes = [];

    skills.forEach(() => {
      const size = getRandomInt(100, 200);
      const position = generateNonOverlappingPosition(size, newPositions, containerSize);
      newPositions.push(position);
      sizes.push(size);
    });

    setPositions(newPositions.map((pos, index) => ({ ...pos, size: sizes[index] })));
  }, []);

  return (
    <Container>
      {positions.length > 0 &&
        skills.map((skillSet, index) => (
          <LargeBubble
            key={index}
            skill={skillSet.skill}
            subSkills={skillSet.subSkills}
            image={skillSet.image}
            size={positions[index].size}
            position={{ x: positions[index].x, y: positions[index].y }}
          />
        ))}
    </Container>
  );
}

export default Skill;
