import React, { useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';
import SmallBubble from './SmallBubble';
import { getRandomInt, generateNonOverlappingPosition } from './utils';

const pulse = keyframes`
  0% {
    transform: scale(1);
    box-shadow: 0 0 5px rgba(0, 123, 255, 0.5);
  }
  50% {
    transform: scale(1.05);
    box-shadow: 0 0 20px rgba(0, 123, 255, 1);
  }
  100% {
    transform: scale(1);
    box-shadow: 0 0 5px rgba(0, 123, 255, 0.5);
  }
`;

const Bubble = styled.div`
  width: ${props => props.size}px;
  height: ${props => props.size}px;
  background-image: url(${props => props.image});
  background-size: cover;
  background-position: center;
  border-radius: 50%;
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  position: absolute;
  top: ${props => props.position.y}px;
  left: ${props => props.position.x}px;
  transition: transform 0.3s, opacity 0.3s;
  animation: ${pulse} 2s infinite;

  &:hover {
    transform: scale(1.1);
  }
`;

const SmallBubblesContainer = styled.div.attrs(props => ({
  style: {
    top: `${props.position.y + props.size / 2}px`,
    left: `${props.position.x + props.size / 2}px`,
  }
}))`
  position: absolute;
  width: ${props => props.size * 2}px;
  height: ${props => props.size * 2}px;
  transform: translate(-50%, -50%);
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
`;

const HighlightArea = styled.div`
  position: absolute;
  width: 100%;
  height: 100%;
  border: 2px dashed rgba(255, 255, 255, 0.5);
  border-radius: 50%;
  top: 0;
  left: 0;
  pointer-events: none;
`;

const LargeBubble = ({ skill, subSkills, image, size, position, existingPositions }) => {
  const [expanded, setExpanded] = useState(false);
  const [smallBubblePositions, setSmallBubblePositions] = useState([]);

  useEffect(() => {
    if (expanded) {
      const containerSize = { width: size/2, height: size/2 };
      const newPositions = [];
      const newSizes = [];

      subSkills.forEach(() => {
        const newSize = getRandomInt(30, 50);
        const newPosition = generateNonOverlappingPosition(newSize, newPositions.concat(existingPositions), containerSize, position);
        newPositions.push(newPosition);
        newSizes.push(newSize);
      });
      setSmallBubblePositions(newPositions.map((pos, index) => ({
        ...pos,
        x: pos.x - size / 2,
        y: pos.y - size / 2,
        size: newSizes[index]
      })));
    }
  }, [expanded, subSkills, size, existingPositions]);

  const handleClick = () => {
    setExpanded(true);
  };

  return (
    <>
      {!expanded && (
        <Bubble onClick={handleClick} image={image} size={size} position={position}>
          {skill}
        </Bubble>
      )}
      {expanded && (
        
        <SmallBubblesContainer size={size}>
          <HighlightArea />
          {smallBubblePositions.length > 0 && subSkills.map((subSkill, index) => (
            <SmallBubble key={index} skill={subSkill} position={smallBubblePositions[index]} />
          ))}
        </SmallBubblesContainer>
      )}
    </>
  );
};

export default LargeBubble;
