import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import LargeBubble from './LargeBubble';
import bubble from './bubble.png';
import { getRandomInt, generateNonOverlappingPosition } from './utils';


const Container = styled.div`
  position: relative;
  width: 100vw;
  height: 100vh;
  background-color: #282c34;
  overflow: hidden;
`;

const skills = [
  {
    skill: 'Artificial Intelligence',
    subSkills: ['Data Analysis', 'Computer Vision', 'PyTorch', 'Deep Learning'],
    image: bubble
  },
  {
    skill: 'Web Development',
    subSkills: ['HTML', 'CSS', 'JavaScript', 'React', 'Node.js'],
    image: bubble
  },
  {
    skill: 'Game Development',
    subSkills: ['Unity', 'Unreal Engine', 'C#', '3D Modeling'],
    image: bubble
  }
];

export function Skill() {
  const [positions, setPositions] = useState([]);
  const [allSmallBubblePositions, setAllSmallBubblePositions] = useState([]);

  useEffect(() => {
    generateAndSavePositions();
  }, []);

  const generateAndSavePositions = () => {
    const containerSize = { width: window.innerWidth, height: window.innerHeight };
    const newPositions = [];
    const sizes = [];
    let attempts = 0;
    let successful = true;

    skills.forEach((skillSet) => {
      const size = skillSet.subSkills.length * 50;
      const {position,overlap} = generateNonOverlappingPosition(size, newPositions, containerSize);
      successful = successful && !overlap;
      newPositions.push(position);
      sizes.push(size);
    });

    if (successful) {
      const finalPositions = newPositions.map((pos, index) => ({ ...pos, size: sizes[index] }));
      setPositions(finalPositions);
      savePositionsToFile(finalPositions);  // Save positions to JSON file
    }
  };

  const savePositionsToFile = (positions) => {
    const jsonString = JSON.stringify(positions);
    localStorage.setItem('./bubbles.json', jsonString);
  };

  const handleSmallBubblePositions = (positions) => {
    setAllSmallBubblePositions(allSmallBubblePositions.concat(positions));
  };

  return (
    <Container>
      {positions.length > 0 && skills.map((skillSet, index) => (
        <LargeBubble
          key={index}
          skill={skillSet.skill}
          subSkills={skillSet.subSkills}
          image={skillSet.image}
          size={positions[index].size}
          position={{ x: positions[index].x, y: positions[index].y }}
          existingPositions={allSmallBubblePositions}
          onSmallBubblePositionsGenerated={handleSmallBubblePositions}
        />
      ))}
    </Container>
  );
}

export default Skill;
