import React from 'react';
import styled, { keyframes } from 'styled-components';

const popIn = keyframes`
  0% {
    transform: scale(0);
    opacity: 0;
  }
  100% {
    transform: scale(1);
    opacity: 1;
  }
`;

// Filter out props that shouldn't be forwarded to the DOM
const Bubble = styled.div.withConfig({
  shouldForwardProp: (prop, defaultValidatorFn) =>
    !['position'].includes(prop) && defaultValidatorFn(prop)
}).attrs(props => ({
  style: {
    width: `${props.position.size}px`,
    height: `${props.position.size}px`,
    top: `${props.position.y}px`,
    left: `${props.position.x}px`,
  }
}))`
  background-color: rgba(0, 200, 100, 0.7);
  border-radius: 50%;
  display: flex;
  justify-content: center;
  align-items: center;
  position: absolute;
  transition: transform 0.3s;
  animation: ${popIn} 0.5s ease-out;

  &:hover {
    transform: scale(1.1);
    box-shadow: 0 0 15px rgba(0, 200, 100, 0.8);
  }
`;

const SmallBubble = ({ skill, position }) => {
  return (
    <Bubble position={position}>
      {skill}
    </Bubble>
  );
};

export default SmallBubble;
